import axios from 'axios';

const API_BASE_URL = process.env.REACT_APP_API_BASE_URL || 'http://localhost:8000';

const api = axios.create({
  baseURL: API_BASE_URL,
  headers: {
    'Content-Type': 'application/json',
  },
});

// 请求拦截器 - 添加认证信息
api.interceptors.request.use(
  (config) => {
    const token = localStorage.getItem('authToken');
    if (token) {
      config.headers.Authorization = `Bearer ${token}`;
    }
    return config;
  },
  (error) => {
    return Promise.reject(error);
  }
);

// 响应拦截器 - 处理错误
api.interceptors.response.use(
  (response) => {
    return response;
  },
  (error) => {
    // 处理401未授权错误
    if (error.response && error.response.status === 401) {
      localStorage.removeItem('authToken');
      // 如果有路由，可以重定向到登录页
      // window.location.href = '/login';
    }
    return Promise.reject(error);
  }
);

// 处理需求相关的API
export const requirementService = {
  // 处理需求 (全流程)
  processRequirement: (text) => {
    return api.post('/api/process', { text });
  },

  // 仅澄清需求
  clarifyRequirement: (text) => {
    return api.post('/api/clarify', { text });
  },

  // 获取所有需求
  getRequirements: () => {
    return api.get('/api/requirements');
  },
};

// 处理期望相关的API
export const expectationService = {
  // 获取所有期望
  getExpectations: () => {
    return api.get('/api/expectations');
  },

  // 获取单个期望详情
  getExpectation: (id) => {
    return api.get(`/api/expectations/${id}`);
  },

  // 创建新期望
  createExpectation: (data) => {
    return api.post('/api/expectations', data)
      .catch(() => {
        // GraphQL 回退
        const createExpectationMutation = `
          mutation CreateExpectation(
            $name: String!
            $description: String
            $acceptance_criteria: [String]
            $constraints: [String]
          ) {
            createExpectation(
              name: $name
              description: $description
              acceptance_criteria: $acceptance_criteria
              constraints: $constraints
            ) {
              expectation {
                id
                name
                description
                acceptance_criteria
                constraints
              }
              success
              message
            }
          }
        `;

        return api.post('/graphql', {
          query: createExpectationMutation,
          variables: {
            name: data.name,
            description: data.description,
            acceptance_criteria: data.acceptanceCriteria || [],
            constraints: data.constraints || []
          }
        }).then(response => {
          if (response.data.errors) {
            throw new Error(response.data.errors[0].message);
          }
          return { 
            data: response.data.data.createExpectation.expectation 
          };
        });
      });
  },
};

// 处理代码生成相关的API
export const generationService = {
  // 根据期望生成代码
  generateCode: (expectationId) => {
    return api.post('/api/generate', { expectation_id: expectationId });
  },

  // 获取生成历史
  getGenerationHistory: () => {
    return api.get('/api/generations');
  },
};

// 处理验证相关的API
export const validationService = {
  // 验证代码是否满足期望
  validateCode: (generationId, expectationId) => {
    return api.post('/api/validate', {
      generation_id: generationId,
      expectation_id: expectationId,
    });
  },

  // 获取验证历史
  getValidationHistory: () => {
    return api.get('/api/validations');
  },
};

// 处理用户认证相关的API
export const authService = {
  // 用户登录
  login: (credentials) => {
    // For development, if login with REST fails, try GraphQL
    return api.post('/api/auth/login', credentials)
      .catch(() => {
        // GraphQL fallback
        const loginMutation = `
          mutation Login($email: String!, $password: String!) {
            login(email: $email, password: $password) {
              token
              user {
                id
                name
                email
              }
            }
          }
        `;
        return api.post('/graphql', {
          query: loginMutation,
          variables: credentials
        }).then(response => {
          if (response.data.errors) {
            throw new Error(response.data.errors[0].message);
          }
          return { data: response.data.data.login };
        });
      });
