import React, { useState } from 'react';

const API_BASE_URL = 'http://localhost:8000';

function Memory({ sessionId }) {
  const [memories, setMemories] = useState([
    {
      id: 'mem-001',
      title: '用户管理系统',
      preview: '一个完整的用户管理系统，提供用户账户生命周期管理和安全访问控制...',
      date: '2025-04-09',
      type: 'expectation'
    },
    {
      id: 'mem-002',
      title: '支付处理系统',
      preview: '安全处理多种支付方式的交易，包括信用卡、电子钱包和银行转账...',
      date: '2025-04-08',
      type: 'expectation'
    },
    {
      id: 'mem-003',
      title: '库存管理系统',
      preview: '跟踪和管理产品库存，支持库存预警和自动补货功能...',
      date: '2025-04-07',
      type: 'expectation'
    },
    {
      id: 'mem-004',
      title: '报表仪表板',
      preview: '生成和展示业务关键指标的可视化报表和图表...',
      date: '2025-04-06',
      type: 'expectation'
    }
  ]);

  const [selectedMemory, setSelectedMemory] = useState(memories[0]);
  const [selectedCategory, setSelectedCategory] = useState('expectation');
  const [searchTerm, setSearchTerm] = useState('');

  const handleCategoryChange = (category) => {
    setSelectedCategory(category);
    // 在实际实现中，这里会根据分类过滤记忆项
  };

  const handleMemoryClick = (memory) => {
    setSelectedMemory(memory);
  };

  const handleSearchChange = (e) => {
    setSearchTerm(e.target.value);
    // 在实际实现中，这里会根据搜索词过滤记忆项
  };

  const handleEditMemory = () => {
    console.log('Edit memory:', selectedMemory.id);
  };

  const handleDeleteMemory = () => {
    console.log('Delete memory:', selectedMemory.id);
  };

  const memoryDetail = {
    title: '用户管理系统',
    description: '一个完整的用户管理系统，提供用户账户生命周期管理和安全访问控制。系统应支持用户注册、认证、个人资料管理和基于角色的权限控制。',
    subExpectations: [
      '用户注册：允许新用户注册账户，收集必要信息并创建用户档案',
      '用户认证：验证用户身份，允许合法用户登录系统',
      '个人资料管理：允许用户查看和更新个人资料信息',
      '基于角色的访问控制：实现基于角色的访问控制'
    ],
    constraints: [
      '所有用户数据处理必须符合数据保护规范',
      '系统应支持至少10,000个并发用户',
      '身份验证响应时间应小于500毫秒'
    ],
    relatedMemories: [
      { id: 'rel-001', title: '用户管理系统代码生成', date: '2025-04-09' },
      { id: 'rel-002', title: '用户管理系统验证结果', date: '2025-04-09' },
      { id: 'rel-003', title: '用户认证模块需求澄清', date: '2025-04-08' }
    ]
  };

  return (
    <div className="memory-container">
      <div className="memory-sidebar">
        <div className="memory-search">
          <input 
            type="text" 
            placeholder="搜索记忆..." 
            value={searchTerm}
            onChange={handleSearchChange}
          />
        </div>

        <div className="memory-categories">
          <h3>分类</h3>
          <div 
            className={`memory-category ${selectedCategory === 'expectation' ? 'active' : ''}`}
            onClick={() => handleCategoryChange('expectation')}
          >
            <span className="material-symbols-rounded">description</span>
            <span>期望</span>
          </div>
          <div 
            className={`memory-category ${selectedCategory === 'code' ? 'active' : ''}`}
            onClick={() => handleCategoryChange('code')}
          >
            <span className="material-symbols-rounded">code</span>
            <span>代码生成</span>
          </div>
          <div 
            className={`memory-category ${selectedCategory === 'validation' ? 'active' : ''}`}
            onClick={() => handleCategoryChange('validation')}
          >
            <span className="material-symbols-rounded">verified</span>
            <span>验证结果</span>
          </div>
          <div 
            className={`memory-category ${selectedCategory === 'clarification' ? 'active' : ''}`}
            onClick={() => handleCategoryChange('clarification')}
          >
            <span className="material-symbols-rounded">psychology</span>
            <span>需求澄清</span>
          </div>
        </div>

        <div className="memory-items">
          <h3>记忆项</h3>
          {memories.map((memory) => (
            <div 
              key={memory.id} 
              className={`memory-item ${selectedMemory && selectedMemory.id === memory.id ? 'active' : ''}`}
              onClick={() => handleMemoryClick(memory)}
            >
              <div className="memory-item-header">
                <div className="memory-item-title">{memory.title}</div>
                <div className="memory-item-date">{memory.date}</div>
              </div>
              <div className="memory-item-preview">{memory.preview}</div>
            </div>
          ))}
        </div>
      </div>

      {selectedMemory && (
        <div className="memory-content">
          <div className="memory-detail-header">
            <div className="memory-detail-title">{memoryDetail.title}</div>
            <div className="memory-detail-actions">
              <button className="secondary-button" onClick={handleEditMemory}>
                <span className="material-symbols-rounded">edit</span>
                <span>编辑</span>
              </button>
              <button className="secondary-button" onClick={handleDeleteMemory}>
                <span className="material-symbols-rounded">delete</span>
                <span>删除</span>
              </button>
            </div>
          </div>

          <div className="memory-detail-meta">
            <span><span className="material-symbols-rounded">calendar_today</span>创建于 {selectedMemory.date}</span>
            <span><span className="material-symbols-rounded">update</span>最后更新 {selectedMemory.date} 14:15</span>
            <span><span className="material-symbols-rounded">category</span>{
              selectedMemory.type === 'expectation' ? '期望' : 
              selectedMemory.type === 'code' ? '代码生成' :
              selectedMemory.type === 'validation' ? '验证结果' : '需求澄清'
            }</span>
          </div>

          <div className="memory-detail-content">
            <h3>{memoryDetail.title}</h3>
            <p>{memoryDetail.description}</p>

            <h4>子期望</h4>
            <ul>
              {memoryDetail.subExpectations.map((subExp, index) => (
                <li key={index}><strong>{subExp.split('：')[0]}</strong>：{subExp.split('：')[1]}</li>
              ))}
            </ul>

            <h4>约束条件</h4>
            <ul>
              {memoryDetail.constraints.map((constraint, index) => (
                <li key={index}>{constraint}</li>
              ))}
            </ul>

            <h4>相关记忆</h4>
            <ul>
              {memoryDetail.relatedMemories.map((memory, index) => (
                <li key={index}>
                  <a href="#" onClick={(e) => {
                    e.preventDefault();
                    console.log('Navigate to related memory:', memory.id);
                  }}>
                    {memory.title} ({memory.date})
                  </a>
                </li>
              ))}
            </ul>
          </div>
        </div>
      )}
    </div>
  );
}

export default Memory;
