import React, { useState, useEffect } from 'react';
import { 
  Box, 
  Flex, 
  VStack, 
  HStack, 
  Text, 
  Button,
  Heading,
  Divider,
  useColorModeValue,
  IconButton,
  Tooltip,
  Menu,
  MenuButton,
  MenuList,
  MenuItem,
  Code,
  Select
} from '@chakra-ui/react';
import { 
  DownloadIcon, 
