    'sh': 'bash',
    'yml': 'yaml',
    'yaml': 'yaml',
    'sql': 'sql',
    'graphql': 'graphql',
    'swift': 'swift',
    'kt': 'kotlin'
  };

  return languageMap[ext] || 'text';
};

/**
 * Êñá‰ª∂Ê†ëÁªÑ‰ª∂
