                borderRadius="md"
                bg={currentPath === selectedFile ? 'blue.50' : 'transparent'}
                _hover={{ bg: currentPath === selectedFile ? 'blue.50' : 'gray.50' }}
                onClick={() => onSelectFile(currentPath)}
                fontSize="sm"
              >
                <HStack>
                  <Text fontSize="lg">üìÑ</Text>
                  <Text>{name}</Text>
                </HStack>
              </Box>
            );
          } else {
            const [isOpen, setIsOpen] = useState(level < 1);

            return (
              <Box key={currentPath}>
                <Box 
                  py={1}
                  px={2}
                  cursor="pointer"
                  onClick={() => setIsOpen(!isOpen)}
                  fontSize="sm"
                >
                  <HStack>
                    <Text fontSize="lg">
                      {isOpen ? 'üìÇ' : 'üìÅ'}
                    </Text>
                    <Text fontWeight="bold">{name}</Text>
                    {isOpen ? <ChevronDownIcon /> : <ChevronRightIcon />}
                  </HStack>
                </Box>

                {isOpen && renderDirectory(value, currentPath, level + 1)}
              </Box>
            );
          }
        })}
      </VStack>
    );
  };

  return (
    <Box 
      overflowY="auto" 
      h="100%" 
      borderWidth="1px" 
      borderRadius="md" 
      p={2}
      bg={useColorModeValue('white', 'gray.800')}
    >
      {renderDirectory(fileTree)}
    </Box>
  );
};

/**
 * ‰ª£Á†ÅÊü•ÁúãÁªÑ‰ª∂
 * @param {Object} props ÁªÑ‰ª∂Â±ûÊÄß
 * @param {Array} props.files ‰ª£Á†ÅÊñá‰ª∂ÂØπË±°Êï∞ÁªÑ
 * @param {Object} props.generation ÁîüÊàêÁªìÊûúÂØπË±°
 */
const CodeViewer = ({ files = [], generation = null }) => {
  const [selectedFile, setSelectedFile] = useState(null);
  const [selectedContent, setSelectedContent] = useState('');
  const [showLineNumbers, setShowLineNumbers] = useState(true);
  const [isDarkMode, setIsDarkMode] = useState(false);

  // ÂàùÂßãÈÄâÊã©Á¨¨‰∏Ä‰∏™Êñá‰ª∂
  useEffect(() => {
    if (files.length > 0 && !selectedFile) {
      setSelectedFile(files[0].path);
      setSelectedContent(files[0].content);
    }
  }, [files, selectedFile]);

  // Êñá‰ª∂ÈÄâÊã©Â§ÑÁêÜ
  const handleFileSelect = (path) => {
    const file = files.find(f => f.path === path);
    if (file) {
      setSelectedFile(path);
      setSelectedContent(file.content);
    }
  };

  // Â§çÂà∂‰ª£Á†ÅÂ§ÑÁêÜ
  const handleCopyCode = () => {
    navigator.clipboard.writeText(selectedContent);
    // ÂèØ‰ª•Ê∑ªÂä†ÊèêÁ§∫Ê∂àÊÅØ
  };

  // ‰∏ãËΩΩÂçï‰∏™Êñá‰ª∂
  const handleDownloadFile = () => {
    const file = files.find(f => f.path === selectedFile);
    if (!file) return;

    const blob = new Blob([file.content], { type: 'text/plain' });
    const url = URL.createObjectURL(blob);
    const a = document.createElement('a');
    a.href = url;
    a.download = selectedFile.split('/').pop();
    document.body.appendChild(a);
    a.click();
    document.body.removeChild(a);
    URL.revokeObjectURL(url);
  };

  // ‰∏ãËΩΩÊâÄÊúâÊñá‰ª∂
  const handleDownloadAll = () => {
    if (generation && generation.id) {
      window.open(`/api/generations/${generation.id}/download`, '_blank');
    }
  };

  return (
    <Box h="full">
      <Flex justify="space-between" align="center" mb={4}>
        <Heading size="md">‰ª£Á†ÅÊü•ÁúãÂô®</Heading>

        <HStack>
          <Select 
            size="sm" 
            value={isDarkMode ? 'dark' : 'light'}
            onChange={(e) => setIsDarkMode(e.target.value === 'dark')}
            w="100px"
          >
            <option value="light">ÊµÖËâ≤</option>
            <option value="dark">Ê∑±Ëâ≤</option>
          </Select>

          <Button 
            size="sm" 
            variant="outline" 
            leftIcon={<DownloadIcon />}
            onClick={handleDownloadAll}
          >
            ‰∏ãËΩΩÂÖ®ÈÉ®
          </Button>
        </HStack>
      </Flex>

      <Flex h="calc(100% - 60px)" borderWidth="1px" borderRadius="md">
        {/* Êñá‰ª∂Ê†ë */}
        <Box w="250px" borderRightWidth="1px">
          <FileTree 
            files={files}
            selectedFile={selectedFile}
            onSelectFile={handleFileSelect}
          />
        </Box>

        {/* ‰ª£Á†ÅÊü•ÁúãÂå∫Âüü */}
        <Box flex="1" position="relative">
          {selectedFile ? (
            <>
              <Flex 
                justify="space-between" 
                align="center" 
                p={2} 
                borderBottomWidth="1px" 
                bg={useColorModeValue('gray.50', 'gray.700')}
              >
                <Text fontWeight="bold" fontSize="sm">{selectedFile}</Text>

                <HStack>
                  <Tooltip label="ÊòæÁ§∫/ÈöêËóèË°åÂè∑">
                    <Button 
                      size="xs" 
                      variant={showLineNumbers ? "solid" : "outline"}
                      onClick={() => setShowLineNumbers(!showLineNumbers)}
                    >
                      Ë°åÂè∑
                    </Button>
                  </Tooltip>

                  <Tooltip label="Â§çÂà∂‰ª£Á†Å">
                    <IconButton 
                      size="xs" 
                      icon={<CopyIcon />}
                      onClick={handleCopyCode}
                      aria-label="Â§çÂà∂‰ª£Á†Å"
                    />
                  </Tooltip>

                  <Tooltip label="‰∏ãËΩΩÊñá‰ª∂">
                    <IconButton 
                      size="xs" 
                      icon={<DownloadIcon />}
                      onClick={handleDownloadFile}
                      aria-label="‰∏ãËΩΩÊñá‰ª∂"
                    />
                  </Tooltip>
                </HStack>
              </Flex>

              <Box overflowY="auto" h="calc(100% - 40px)" fontSize="sm">
                <SyntaxHighlighter
                  language={getLanguageFromFilename(selectedFile)}
                  style={isDarkMode ? atomDark : prism}
                  showLineNumbers={showLineNumbers}
                  customStyle={{
                    margin: 0,
                    padding: '10px',
                    height: '100%'
                  }}
                >
                  {selectedContent}
                </SyntaxHighlighter>
              </Box>
            </>
          ) : (
            <Flex h="100%" justify="center" align="center">
              <Text color="gray.500">ËØ∑ÈÄâÊã©Êñá‰ª∂Êü•Áúã‰ª£Á†Å</Text>
            </Flex>
          )}
        </Box>
      </Flex>
    </Box>
  );
};

export default CodeViewer;
