import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import {
  Box,
  Button,
  FormControl,
  FormLabel,
  FormHelperText,
  Input,
  Textarea,
  VStack,
  HStack,
  Heading,
  Text,
  useToast,
  IconButton,
  Divider,
  Tag,
  TagLabel,
  TagCloseButton,
  Flex,
  Breadcrumb,
  BreadcrumbItem,
  BreadcrumbLink,
} from '@chakra-ui/react';
import { ChevronRightIcon, AddIcon } from '@chakra-ui/icons';
import { useExpeta } from '../context/ExpetaContext';

const CreateExpectationPage = () => {
  const { createExpectation } = useExpeta();
  const navigate = useNavigate();
  const toast = useToast();

  // 表单状态
  const [formData, setFormData] = useState({
    name: '',
    description: '',
    acceptanceCriteria: [],
    constraints: []
  });

  // 临时状态用于新增条目
  const [newCriterion, setNewCriterion] = useState('');
  const [newConstraint, setNewConstraint] = useState('');

  // 提交状态
  const [isSubmitting, setIsSubmitting] = useState(false);

  // 处理输入变化
  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
  };

  // 添加验收标准
  const handleAddCriterion = () => {
    if (newCriterion.trim()) {
      setFormData(prev => ({
        ...prev,
        acceptanceCriteria: [...prev.acceptanceCriteria, newCriterion.trim()]
      }));
      setNewCriterion('');
    }
  };

  // 删除验收标准
  const handleRemoveCriterion = (index) => {
    setFormData(prev => ({
      ...prev,
      acceptanceCriteria: prev.acceptanceCriteria.filter((_, i) => i !== index)
    }));
  };

  // 添加约束条件
  const handleAddConstraint = () => {
    if (newConstraint.trim()) {
      setFormData(prev => ({
        ...prev,
        constraints: [...prev.constraints, newConstraint.trim()]
      }));
      setNewConstraint('');
    }
  };

  // 删除约束条件
  const handleRemoveConstraint = (index) => {
    setFormData(prev => ({
      ...prev,
      constraints: prev.constraints.filter((_, i) => i !== index)
    }));
  };

  // 表单提交
  const handleSubmit = async (e) => {
    e.preventDefault();

    // 验证表单
    if (!formData.name.trim()) {
      toast({
        title: '请输入期望名称',
        status: 'error',
        duration: 3000,
        isClosable: true
      });
      return;
    }

    setIsSubmitting(true);

    try {
      // 如果有真实的API可以调用
      if (createExpectation) {
        const result = await createExpectation(formData);
        toast({
          title: '期望创建成功',
          status: 'success',
          duration: 3000,
          isClosable: true
        });

        // 导航到新创建的期望详情页
        if (result && result.id) {
          navigate(`/expectations/${result.id}`);
        } else {
          navigate('/expectations');
        }
      } else {
        // 开发模式：模拟API响应
        setTimeout(() => {
          toast({
            title: '期望创建成功（模拟）',
            description: '实际环境中期望将被保存到数据库',
            status: 'success',
            duration: 3000,
            isClosable: true
          });
          navigate('/expectations');
        }, 1000);
      }
    } catch (error) {
      toast({
        title: '创建失败',
        description: error.message || '发生未知错误，请稍后再试',
        status: 'error',
        duration: 5000,
        isClosable: true
      });
    } finally {
      setIsSubmitting(false);
    }
  };

  return (
    <Box maxW="900px" mx="auto" pb={10}>
      <Breadcrumb separator={<ChevronRightIcon color="gray.500" />} mb={6}>
        <BreadcrumbItem>
          <BreadcrumbLink onClick={() => navigate('/')}>首页</BreadcrumbLink>
        </BreadcrumbItem>
        <BreadcrumbItem>
          <BreadcrumbLink onClick={() => navigate('/expectations')}>期望列表</BreadcrumbLink>
        </BreadcrumbItem>
        <BreadcrumbItem isCurrentPage>
          <BreadcrumbLink>创建新期望</BreadcrumbLink>
        </BreadcrumbItem>
      </Breadcrumb>

      <Heading size="lg" mb={6}>创建新期望</Heading>

      <form onSubmit={handleSubmit}>
        <VStack spacing={6} align="stretch">
          <FormControl isRequired>
            <FormLabel>期望名称</FormLabel>
            <Input
              name="name"
              value={formData.name}
              onChange={handleInputChange}
              placeholder="输入期望名称"
            />
            <FormHelperText>给予简洁明了的名称以便于识别</FormHelperText>
          </FormControl>

          <FormControl>
            <FormLabel>期望描述</FormLabel>
            <Textarea
              name="description"
              value={formData.description}
              onChange={handleInputChange}
              placeholder="详细描述期望的功能和目的"
              rows={4}
            />
          </FormControl>

          <Divider />

          <Box>
            <FormLabel>验收标准</FormLabel>
            <Text fontSize="sm" color="gray.600" mb={3}>
              列出所有必须满足的验收标准，这些将用于验证代码是否符合期望
            </Text>

            <VStack spacing={2} align="stretch" mb={4}>
              {formData.acceptanceCriteria.map((criterion, index) => (
                <Tag size="lg" key={index} borderRadius="full" variant="solid" colorScheme="blue">
                  <TagLabel>{criterion}</TagLabel>
                  <TagCloseButton onClick={() => handleRemoveCriterion(index)} />
                </Tag>
              ))}
            </VStack>

            <HStack>
              <Input
                placeholder="添加新的验收标准"
                value={newCriterion}
                onChange={(e) => setNewCriterion(e.target.value)}
                onKeyPress={(e) => e.key === 'Enter' && (e.preventDefault(), handleAddCriterion())}
              />
              <IconButton
                icon={<AddIcon />}
                onClick={handleAddCriterion}
                aria-label="Add criterion"
                colorScheme="blue"
              />
            </HStack>
          </Box>

          <Divider />

          <Box>
            <FormLabel>约束条件</FormLabel>
            <Text fontSize="sm" color="gray.600" mb={3}>
              列出所有必须遵守的约束条件，例如技术栈、性能要求等
            </Text>

            <VStack spacing={2} align="stretch" mb={4}>
              {formData.constraints.map((constraint, index) => (
                <Tag size="lg" key={index} borderRadius="full" variant="solid" colorScheme="purple">
                  <TagLabel>{constraint}</TagLabel>
                  <TagCloseButton onClick={() => handleRemoveConstraint(index)} />
                </Tag>
              ))}
            </VStack>

            <HStack>
              <Input
                placeholder="添加新的约束条件"
                value={newConstraint}
                onChange={(e) => setNewConstraint(e.target.value)}
                onKeyPress={(e) => e.key === 'Enter' && (e.preventDefault(), handleAddConstraint())}
              />
              <IconButton
                icon={<AddIcon />}
                onClick={handleAddConstraint}
                aria-label="Add constraint"
                colorScheme="purple"
              />
            </HStack>
          </Box>

          <Divider />

          <Flex justify="space-between">
            <Button 
              variant="outline" 
              onClick={() => navigate('/expectations')}
            >
              取消
            </Button>
            <Button 
              colorScheme="blue" 
              type="submit" 
              isLoading={isSubmitting}
              loadingText="创建中..."
            >
              创建期望
            </Button>
          </Flex>
        </VStack>
      </form>
    </Box>
  );
};

export default CreateExpectationPage;
