function ProcessTab() {
  const [requirement, setRequirement] = useState('');
  const [processing, setProcessing] = useState(false);
  const [error, setError] = useState(null);
  const toast = useToast();

  const handleProcess = async () => {
    if (!requirement.trim()) {
      toast({
        title: 'Error',
        description: 'Please enter a requirement',
        status: 'error',
        duration: 3000,
        isClosable: true
      });
      return;
    }

    setProcessing(true);
    setError(null);

    try {
      await new Promise(resolve => setTimeout(resolve, 2000));

      toast({
        title: 'Success',
        description: 'Requirement processed successfully',
        status: 'success',
        duration: 3000,
        isClosable: true
      });
    } catch (err) {
      setError(err.message || 'An error occurred while processing the requirement');

      toast({
        title: 'Error',
        description: err.message || 'An error occurred while processing the requirement',
        status: 'error',
        duration: 5000,
        isClosable: true
      });
    } finally {
      setProcessing(false);
    }
  };

  return (
    <Box>
      <Heading size="md" mb={6}>Process Requirement</Heading>

      <Box bg="white" p={6} borderRadius="md" shadow="md" mb={6}>
        <Text mb={2}>Enter your requirement:</Text>
        <Textarea
          value={requirement}
          onChange={(e) => setRequirement(e.target.value)}
          placeholder="Describe what you want to build..."
          size="lg"
          rows={5}
          mb={4}
        />
        <Button
          colorScheme="blue"
          onClick={handleProcess}
          isLoading={processing}
          loadingText="Processing"
        >
          Process Requirement
        </Button>
      </Box>

      {error && (
        <Alert status="error" mb={6}>
          <AlertIcon />
          {error}
        </Alert>
      )}
    </Box>
  );
}

