function GenerateTab() {
  const [expectationId, setExpectationId] = useState('');
  const [processing, setProcessing] = useState(false);
  const [error, setError] = useState(null);
  const [generatedFiles, setGeneratedFiles] = useState([]);
  const [expectation, setExpectation] = useState(null);
  const [generationStatus, setGenerationStatus] = useState('');
  const [generationProgress, setGenerationProgress] = useState(0);
  const [streamingFiles, setStreamingFiles] = useState([]);
  const toast = useToast();

  const handleGenerate = async () => {
    if (!expectationId.trim()) {
      toast({
        title: 'Error',
        description: 'Please enter an expectation ID',
        status: 'error',
        duration: 3000,
        isClosable: true
      });
      return;
    }

    setProcessing(true);
    setError(null);
    setGeneratedFiles([]);
    setExpectation(null);

    try {
      const expectationResponse = await fetch(`${API_BASE_URL}/memory/expectations/${expectationId}`);

      if (!expectationResponse.ok) {
        throw new Error(`Failed to fetch expectation: ${expectationResponse.statusText}`);
      }

      const expectationData = await expectationResponse.json();

      let generationData;
      const generationResponse = await fetch(`${API_BASE_URL}/memory/generations/${expectationId}`);

      if (generationResponse.ok) {
        generationData = await generationResponse.json();
      } else {
        setGenerationStatus('Starting code generation...');
        setGenerationProgress(0);
        setStreamingFiles([]);

        const eventSource = new EventSource(`${API_BASE_URL}/generate/stream/${expectationId}`);

        let receivedData = false;

        eventSource.onmessage = (event) => {
          receivedData = true;
          const data = JSON.parse(event.data);

          if (data.status) {
            setGenerationStatus(data.message || data.status);

            switch(data.status) {
              case 'extracting_concepts':
                setGenerationProgress(10);
                break;
              case 'extracting_constraints':
                setGenerationProgress(20);
                break;
              case 'generating_code':
                setGenerationProgress(40);
                break;
              case 'validating_code':
                setGenerationProgress(80);
                break;
              case 'fixing_issues':
                setGenerationProgress(90);
                break;
              case 'completed':
                setGenerationProgress(100);
                break;
              default:
                setGenerationProgress(prev => Math.min(prev + 5, 95));
            }
          }

          if (data.files) {
            setStreamingFiles(data.files);
          }

          if (data.status === 'completed') {
            eventSource.close();
          }
        };

        eventSource.onerror = (error) => {
          console.error('EventSource error:', error);
          eventSource.close();

          if (!receivedData) {
            fallbackToRegularApi();
          }
        };

        const fallbackToRegularApi = async () => {
          try {
            const generateResponse = await fetch(`${API_BASE_URL}/generate`, {
              method: 'POST',
              headers: {
                'Content-Type': 'application/json',
              },
              body: JSON.stringify(expectationData),
            });

            if (!generateResponse.ok) {
              throw new Error(`Failed to generate code: ${generateResponse.statusText}`);
            }

            generationData = await generateResponse.json();

            setGeneratedFiles(generationData.files);
            setGenerationStatus('Completed');
            setGenerationProgress(100);
          } catch (err) {
            throw new Error(`Failed to generate code: ${err.message}`);
          }
        };

        await new Promise((resolve, reject) => {
          const timeoutId = setTimeout(() => {
            eventSource.close();
            if (!receivedData) {
              fallbackToRegularApi().then(resolve).catch(reject);
            } else {
              resolve();
            }
          }, 30000);

          eventSource.addEventListener('completed', () => {
            clearTimeout(timeoutId);
            resolve();
          });
        });

        const finalResponse = await fetch(`${API_BASE_URL}/memory/generations/${expectationId}`);
        if (finalResponse.ok) {
          generationData = await finalResponse.json();
        }
      }

      const data = {
        success: true,
        expectation: expectationData,
        generated_code: generationData
      };

      setExpectation(data.expectation);
      setGeneratedFiles(data.generated_code.files);

      toast({
        title: 'Success',
        description: 'Code generated successfully',
        status: 'success',
        duration: 3000,
        isClosable: true
      });
    } catch (err) {
      setError(err.message || 'An error occurred while generating code');

      toast({
        title: 'Error',
        description: err.message || 'An error occurred while generating code',
        status: 'error',
        duration: 5000,
        isClosable: true
      });
    } finally {
      setProcessing(false);
    }
  };

  const handleDownloadFile = (file) => {
    const downloadUrl = `${API_BASE_URL}/download/file/${expectationId}/${file.name}`;

    const a = document.createElement('a');
    a.href = downloadUrl;
    a.download = file.name;
    document.body.appendChild(a);
    a.click();

    document.body.removeChild(a);

    toast({
      title: 'Download Started',
      description: `Downloading ${file.name}`,
      status: 'info',
      duration: 2000,
      isClosable: true
    });
  };

  const handleDownloadAll = () => {
    if (!generatedFiles.length) return;

    const downloadUrl = `${API_BASE_URL}/download/code/${expectationId}`;

    const a = document.createElement('a');
    a.href = downloadUrl;
    a.download = `code_${expectationId}.zip`;
    document.body.appendChild(a);
    a.click();

    document.body.removeChild(a);

    toast({
      title: 'Download Started',
      description: 'Downloading all files as ZIP',
      status: 'info',
      duration: 2000,
      isClosable: true
    });
  };

  const handleDownloadExpectation = () => {
    if (!expectation) return;

    const downloadUrl = `${API_BASE_URL}/download/expectation/${expectationId}?format=yaml`;

    const a = document.createElement('a');
    a.href = downloadUrl;
    a.download = `expectation_${expectationId}.yaml`;
    document.body.appendChild(a);
    a.click();

    document.body.removeChild(a);

    toast({
      title: 'Download Started',
      description: `Downloading expectation YAML`,
      status: 'info',
      duration: 2000,
      isClosable: true
    });
  };

  return (
    <Box>
      <Heading size="md" mb={6}>Generate Code</Heading>

      <Box bg="white" p={6} borderRadius="md" shadow="md" mb={6}>
        <Text mb={2}>Enter expectation ID:</Text>
        <Input
          value={expectationId}
          onChange={(e) => setExpectationId(e.target.value)}
          placeholder="e.g., exp-12345678"
          mb={4}
        />

        <Button
          colorScheme="purple"
          onClick={handleGenerate}
          isLoading={processing}
          loadingText="Generating"
          mr={4}
        >
          Generate Code
        </Button>

        {generatedFiles.length > 0 && (
          <Button
            colorScheme="teal"
            onClick={handleDownloadAll}
            ml={2}
          >
            Download All Files
          </Button>
        )}

        {expectation && (
          <Button
            colorScheme="blue"
            onClick={handleDownloadExpectation}
            ml={2}
          >
            Download Expectation
          </Button>
        )}
      </Box>

      {error && (
        <Alert status="error" mb={6}>
          <AlertIcon />
          {error}
        </Alert>
      )}

      {/* Generation Progress Display */}
      {processing && (
        <Box bg="white" p={6} borderRadius="md" shadow="md" mb={6}>
          <Heading size="sm" mb={4}>Generation Progress</Heading>
          <Text mb={2}>{generationStatus}</Text>
          <Progress value={generationProgress} colorScheme="blue" size="sm" mb={4} />

          {streamingFiles.length > 0 && (
            <Box mt={4}>
              <Text fontWeight="bold">Files being generated:</Text>
              <List spacing={2} mt={2}>
                {streamingFiles.map((file, index) => (
                  <ListItem key={index}>
                    <HStack>
                      <Icon as={FiFile} />
                      <Text>{file.path || `file${index+1}`}</Text>
                    </HStack>
                  </ListItem>
                ))}
              </List>
            </Box>
          )}
        </Box>
      )}

      {expectation && (
        <Box bg="white" p={6} borderRadius="md" shadow="md" mb={6}>
          <Heading size="sm" mb={3}>Expectation: {expectation.name}</Heading>
          <Text mb={2}>{expectation.description}</Text>

          <Text fontWeight="bold" mt={4} mb={2}>Acceptance Criteria:</Text>
          {expectation.acceptance_criteria.map((criterion, index) => (
            <Text key={index} ml={4}> {criterion}</Text>
          ))}
        </Box>
      )}

      {generatedFiles.length > 0 && (
        <Box bg="white" p={6} borderRadius="md" shadow="md">
          <Heading size="sm" mb={4}>Generated Files</Heading>

          {generatedFiles.map((file, index) => (
            <Box 
              key={index} 
              p={4} 
              borderWidth="1px" 
              borderRadius="md" 
              mb={4}
            >
              <Flex justify="space-between" align="center" mb={2}>
                <Text fontWeight="bold">{file.name}</Text>
                <Button
                  size="sm"
                  colorScheme="teal"
                  onClick={() => handleDownloadFile(file)}
                >
                  Download
                </Button>
              </Flex>

              <Box
                bg="gray.50"
                p={3}
                borderRadius="md"
                fontFamily="monospace"
                fontSize="sm"
                whiteSpace="pre-wrap"
                overflowX="auto"
              >
                {file.content}
              </Box>
            </Box>
          ))}
        </Box>
      )}
    </Box>
  );
}

