import React, { useState, useEffect, useRef } from 'react';

const API_BASE_URL = 'http://localhost:8000';

function Requirements({ sessionId }) {
  const [requirementText, setRequirementText] = useState(
    '创建一个用户管理系统，支持用户注册、登录和个人资料管理。系统需要安全存储用户数据，并支持权限控制。'
  );
  const [clarificationData, setClarificationData] = useState({
    top_level_expectation: null,
    sub_expectations: [],
    constraints: []
  });
  const [activeStep, setActiveStep] = useState(1);
  const [chatMessages, setChatMessages] = useState([]);
  const [newMessage, setNewMessage] = useState('');
  const [isTyping, setIsTyping] = useState(false);
  const [conversationId, setConversationId] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  const messagesEndRef = useRef(null);

  useEffect(() => {
    if (messagesEndRef.current) {
      messagesEndRef.current.scrollIntoView({ behavior: 'smooth' });
    }
  }, [chatMessages, isTyping]);

  useEffect(() => {
    if (sessionId) {
      // Initialize chat with system message
      setChatMessages([{
        role: 'system',
        content: '欢迎使用Expeta 2.0交互式澄清功能。我将帮助您更精确地定义需求和期望。',
        time: new Date().toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' })
      }]);
    }
  }, [sessionId]);

  const handleClarifyRequirement = async () => {
    if (!sessionId || !requirementText.trim()) return;

    try {
      setLoading(true);
      setError(null);

      const response = await fetch(`${API_BASE_URL}/clarify`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'Session-ID': sessionId
        },
        body: JSON.stringify({
          requirement: requirementText
        })
      });

      if (response.ok) {
        const data = await response.json();
        setClarificationData(data);
        setActiveStep(2);
        setConversationId(data.conversation_id);

        // Add system message about successful clarification
        setChatMessages(prev => [...prev, {
          role: 'system',
          content: '我已经基于您的需求创建了初步的期望模型。请查看右侧的期望详情，我们可以继续讨论以完善它。',
          time: new Date().toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' })
        }]);
      } else {
        const errorData = await response.json();
        setError(errorData.error || '需求澄清过程中出现问题');
      }
    } catch (error) {
      console.error('Error clarifying requirement:', error);
      setError('网络错误，请重试');
    } finally {
      setLoading(false);
    }
  };

  const handleSendMessage = async () => {
    if (!newMessage.trim() || !sessionId || !conversationId) return;

    const userMessage = {
      role: 'user',
      content: newMessage,
      time: new Date().toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' })
    };

    setChatMessages(prev => [...prev, userMessage]);
    setNewMessage('');
    setIsTyping(true);

    try {
      const response = await fetch(`${API_BASE_URL}/clarify/continue`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'Session-ID': sessionId
        },
        body: JSON.stringify({
          message: userMessage.content,
          conversation_id: conversationId
        })
      });

      if (response.ok) {
        const data = await response.json();
        const systemMessage = {
          role: 'system',
          content: data.response,
          time: new Date().toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' })
        };
        setChatMessages(prev => [...prev, systemMessage]);

        if (data.updated_expectation) {
          setClarificationData(prev => ({
            ...prev,
            ...data.updated_expectation
          }));
        }
      } else {
        const errorData = await response.json();
        setChatMessages(prev => [...prev, {
          role: 'system',
          content: `错误: ${errorData.error || '消息处理失败'}`,
          time: new Date().toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' })
        }]);
      }
    } catch (error) {
      console.error('Error sending message:', error);
      setChatMessages(prev => [...prev, {
        role: 'system',
        content: '网络错误，请重试',
        time: new Date().toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' })
      }]);
    } finally {
      setIsTyping(false);
    }
  };

  const handleKeyDown = (e) => {
    if (e.key === 'Enter' && !e.shiftKey) {
      e.preventDefault();
      handleSendMessage();
    }
  };

  const handleGenerateExpectation = async () => {
    if (!sessionId || !conversationId) return;

    try {
      setLoading(true);
      setError(null);

      const response = await fetch(`${API_BASE_URL}/clarify/finalize`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'Session-ID': sessionId
        },
        body: JSON.stringify({
          conversation_id: conversationId
        })
      });

      if (response.ok) {
        const data = await response.json();
        setClarificationData(data);
        setActiveStep(3);

        // Add system message about successful finalization
        setChatMessages(prev => [...prev, {
          role: 'system',
          content: '期望模型已完成并存储。现在您可以继续处理代码生成。',
          time: new Date().toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' })
        }]);
      } else {
        const errorData = await response.json();
        setError(errorData.error || '期望生成过程中出现问题');
      }
    } catch (error) {
      console.error('Error finalizing expectation:', error);
      setError('网络错误，请重试');
    } finally {
      setLoading(false);
    }
  };

  return (
    <>
      {/* 工作流程可视化 */}
      <section className="workflow-section">
        <div className="section-header">
          <h2>处理流程</h2>
          <div className="section-actions">
            <button className="secondary-button">
              <span className="material-symbols-rounded">refresh</span>
              <span>刷新</span>
            </button>
          </div>
        </div>
        <div className="workflow-visualization">
          <div className={`workflow-step ${activeStep >= 1 ? 'active' : ''}`}>
            <div className="step-icon">
              <span className="material-symbols-rounded">description</span>
            </div>
            <div className="step-label">需求输入</div>
            <div className="step-connector"></div>
          </div>
          <div className={`workflow-step ${activeStep >= 2 ? 'active' : activeStep === 1 ? 'in-progress' : ''}`}>
            <div className="step-icon">
              <span className="material-symbols-rounded">psychology</span>
            </div>
            <div className="step-label">需求澄清</div>
            <div className="step-connector"></div>
          </div>
          <div className={`workflow-step ${activeStep >= 3 ? 'active' : activeStep === 2 ? 'in-progress' : ''}`}>
            <div className="step-icon">
              <span className="material-symbols-rounded">format_list_bulleted</span>
            </div>
            <div className="step-label">期望生成</div>
            <div className="step-connector"></div>
          </div>
          <div className={`workflow-step ${activeStep >= 4 ? 'active' : activeStep === 3 ? 'in-progress' : ''}`}>
            <div className="step-icon">
              <span className="material-symbols-rounded">code</span>
            </div>
            <div className="step-label">代码生成</div>
            <div className="step-connector"></div>
          </div>
          <div className={`workflow-step ${activeStep >= 5 ? 'active' : activeStep === 4 ? 'in-progress' : ''}`}>
            <div className="step-icon">
              <span className="material-symbols-rounded">verified</span>
            </div>
            <div className="step-label">代码验证</div>
          </div>
        </div>
      </section>

      {/* 需求输入和澄清区域 */}
      <section className="requirement-section">
        <div className="section-header">
          <h2>需求处理</h2>
          <div className="section-actions">
            <button className="primary-button">
              <span className="material-symbols-rounded">save</span>
              <span>保存</span>
            </button>
          </div>
        </div>
        <div className="requirement-container">
          <div className="requirement-input">
            <h3>需求输入</h3>
            <div className="input-area">
              <textarea 
                placeholder="请输入您的需求描述..." 
                rows="6" 
                value={requirementText}
                onChange={(e) => setRequirementText(e.target.value)}
              />
              <div className="input-actions">
                <button className="secondary-button">
                  <span className="material-symbols-rounded">upload_file</span>
                  <span>上传文件</span>
                </button>
                <button 
                  className="primary-button clarify-button"
                  onClick={handleClarifyRequirement}
                >
                  <span className="material-symbols-rounded">psychology</span>
                  <span>澄清需求</span>
                </button>
              </div>
            </div>
          </div>
          <div className="clarification-output">
            <h3>需求澄清</h3>
            <div className="output-area">
              <div className="clarification-item">
                <div className="item-header">
                  <span className="item-title">顶层期望</span>
                  <span className="item-status completed">已完成</span>
                </div>
                <div className="item-content">
                  <p><strong>ID:</strong> {clarificationData.top_level_expectation?.id}</p>
                  <p><strong>描述:</strong> {clarificationData.top_level_expectation?.description}</p>
                </div>
              </div>
              <div className="clarification-item">
                <div className="item-header">
                  <span className="item-title">子期望</span>
                  <span className="item-status in-progress">处理中</span>
                </div>
                <div className="item-content">
                  {clarificationData.sub_expectations.map((subExp, index) => (
                    <div key={index} className="sub-expectation">
                      <p><strong>ID:</strong> {subExp.id}</p>
                      <p><strong>描述:</strong> {subExp.description}</p>
                      <div className="criteria-list">
                        <p><strong>标准:</strong></p>
                        <ul>
                          {subExp.criteria.map((criteria, critIndex) => (
                            <li key={critIndex}>{criteria}</li>
                          ))}
                        </ul>
                      </div>
                    </div>
                  ))}
                </div>
              </div>
              <div className="clarification-item">
                <div className="item-header">
                  <span className="item-title">约束条件</span>
                  <span className="item-status completed">已完成</span>
                </div>
                <div className="item-content">
                  <ul className="constraints-list">
                    {clarificationData.constraints.map((constraint, index) => (
                      <li key={index}>{constraint}</li>
                    ))}
                  </ul>
                </div>
              </div>
            </div>
          </div>
        </div>
      </section>

      {/* 聊天交互区域 */}
      <section className="chat-section">
        <div className="section-header">
          <h2>交互式澄清</h2>
          <div className="section-actions">
            <button className="secondary-button">
              <span className="material-symbols-rounded">history</span>
              <span>历史记录</span>
            </button>
          </div>
        </div>
        <div className="chat-container">
          <div className="chat-messages">
            {chatMessages.map((message, index) => (
              <div 
                key={index} 
                className={`message ${message.role === 'user' ? 'user-message' : 'system-message'}`}
              >
                <div className="message-content">
                  <p>{message.content}</p>
                </div>
                <div className="message-time">{message.time}</div>
              </div>
            ))}
            {isTyping && (
              <div className="typing-indicator">
                <span></span>
                <span></span>
                <span></span>
              </div>
            )}
            <div ref={messagesEndRef} />
          </div>
          <div className="chat-input">
            <textarea 
              placeholder="输入您的回复..." 
              rows="1"
              value={newMessage}
              onChange={(e) => setNewMessage(e.target.value)}
              onKeyDown={handleKeyDown}
            />
            <button className="send-button" onClick={handleSendMessage}>
              <span className="material-symbols-rounded">send</span>
            </button>
          </div>
        </div>
      </section>
    </>
  );
}

export default Requirements;
