import React, { useState, useEffect, useRef } from 'react';
import { 
  Box, 
  VStack, 
  HStack, 
  Text, 
  Divider, 
  Alert,
  AlertIcon,
  AlertTitle,
  AlertDescription,
  Button,
  useColorModeValue,
  Flex,
  Heading,
  Badge,
  Progress
} from '@chakra-ui/react';
import MessageList from './MessageList';
import MessageInput from './MessageInput';
import { ConversationStates, getStateDescription } from '../../../shared/states/ConversationStates';

/**
 * ÂØπËØùÈù¢ÊùøÁªÑ‰ª∂ÔºåÂÆûÁé∞Áî®Êà∑‰∏éÁ≥ªÁªüÁöÑÂØπËØù‰∫§‰∫í
 * @param {Object} props ÁªÑ‰ª∂Â±ûÊÄß
 * @param {string} props.conversationId ÂΩìÂâç‰ºöËØùID
 * @param {Array} props.initialMessages ÂàùÂßãÊ∂àÊÅØÂàóË°®
 * @param {Function} props.onSendMessage ÂèëÈÄÅÊ∂àÊÅØÁöÑÂõûË∞ÉÂáΩÊï∞
 */
const ConversationPanel = ({ 
  conversationId,
  initialMessages = [],
  initialState = ConversationStates.INITIAL,
  initialExpectation = null,
  onSendMessage,
  onGenerateCode,
  onConfirmExpectation,
  onEditExpectation,
  loading = false
}) => {
  const [messages, setMessages] = useState(initialMessages);
  const [state, setState] = useState(initialState);
  const [currentExpectation, setCurrentExpectation] = useState(initialExpectation);

  // Áä∂ÊÄÅËÉåÊôØËâ≤
  const stateBgColors = {
    [ConversationStates.INITIAL]: 'gray.100',
    [ConversationStates.COLLECTING_REQUIREMENT]: 'blue.50',
    [ConversationStates.CLARIFYING]: 'blue.100',
    [ConversationStates.EXPECTATION_DRAFT]: 'green.50',
    [ConversationStates.EXPECTATION_CONFIRMED]: 'green.100',
    [ConversationStates.GENERATING]: 'purple.50',
    [ConversationStates.REVIEWING]: 'orange.50',
    [ConversationStates.COMPLETED]: 'teal.50'
  };

  // Áä∂ÊÄÅÂõæÊ†á
  const stateIcons = {
    [ConversationStates.INITIAL]: 'üÜï',
    [ConversationStates.COLLECTING_REQUIREMENT]: 'üìù',
    [ConversationStates.CLARIFYING]: 'üîç',
    [ConversationStates.EXPECTATION_DRAFT]: 'üìã',
    [ConversationStates.EXPECTATION_CONFIRMED]: '‚úÖ',
    [ConversationStates.GENERATING]: '‚öôÔ∏è',
