    if (conversationId) {
      // ËÆ¢ÈòÖWebSocketÊõ¥Êñ∞
      const subscription = subscribeToConversation(conversationId, (update) => {
        if (update.messages) {
          setMessages(update.messages);
        }
        if (update.state) {
          setState(update.state);
        }
        if (update.currentExpectation) {
          setCurrentExpectation(update.currentExpectation);
        }
      });

      return () => {
        // ÂèñÊ∂àËÆ¢ÈòÖ
        if (subscription && subscription.unsubscribe) {
          subscription.unsubscribe();
        }
      };
    }
  }, [conversationId]);

  // Ê®°ÊãüËÆ¢ÈòÖÂáΩÊï∞ÔºåÂÆûÈôÖÈ°πÁõÆ‰∏≠Â∫îËØ•Áî±WebSocketÊúçÂä°Êèê‰æõ
  const subscribeToConversation = (id, callback) => {
    console.log(`ËÆ¢ÈòÖ‰ºöËØùÊõ¥Êñ∞: ${id}`);
    // Ê®°ÊãüÂÆûÁé∞ÔºåÂÆûÈôÖÂ∫îËØ•ËøîÂõû‰∏Ä‰∏™ÂèØÂèñÊ∂àÁöÑËÆ¢ÈòÖ
    return {
      unsubscribe: () => console.log(`ÂèñÊ∂àËÆ¢ÈòÖ‰ºöËØù: ${id}`)
    };
  };

  // Â§ÑÁêÜÂèëÈÄÅÊ∂àÊÅØ
  const handleSendMessage = async (messageText) => {
    if (!onSendMessage) return;

    // ÂÖàÊú¨Âú∞Ê∑ªÂä†Áî®Êà∑Ê∂àÊÅØ‰ª•Êèê‰æõÂç≥Êó∂ÂèçÈ¶à
    const userMessage = {
      id: `local_${Date.now()}`,
      sender: 'user',
      content: messageText,
      timestamp: new Date().toISOString()
    };

    setMessages(prev => [...prev, userMessage]);

    try {
      // Ë∞ÉÁî®Áà∂ÁªÑ‰ª∂Êèê‰æõÁöÑÂèëÈÄÅÊ∂àÊÅØÂáΩÊï∞
      await onSendMessage(messageText);
    } catch (error) {
      // ÊòæÁ§∫ÈîôËØØÊàñÊí§ÈîÄÊú¨Âú∞Ê∑ªÂä†ÁöÑÊ∂àÊÅØ
      console.error('ÂèëÈÄÅÊ∂àÊÅØÂ§±Ë¥•:', error);
