/**
 * @file ConversationPage.js
 * @description 对话页面，集成对话组件和功能
 * 
 * 需求:
 * 1. 作为主要的需求收集和澄清界面
 * 2. 集成ConversationPanel组件
 * 3. 处理会话创建和加载
 * 4. 提供会话状态反馈
 * 5. 当期望确认后，提供进入代码生成的选项
 */

import React, { useState, useEffect } from 'react';
import { 
  Box, 
  Heading, 
  Button, 
  Flex, 
  useToast,
  VStack,
  HStack,
  Text,
  Divider,
  Container,
  SimpleGrid,
  useDisclosure,
  IconButton,
  Breadcrumb,
  BreadcrumbItem,
  BreadcrumbLink
} from '@chakra-ui/react';
import { AddIcon, ChevronRightIcon } from '@chakra-ui/icons';
import { useNavigate, useParams } from 'react-router-dom';
import { ConversationPanel } from '../components/conversation/ConversationPanel';
import { useConversation } from '../hooks/useConversation';
import { ConversationStates } from '../../shared/states/ConversationStates';

/**
 * 对话页面组件
 */
const ConversationPage = () => {
  const { conversationId } = useParams();
  const navigate = useNavigate();
  const toast = useToast();

  const { 
    conversation, 
    messages, 
    sendMessage, 
    startNewConversation,
    confirmExpectation,
    editExpectation,
    generateCode,
    loading,
    error
  } = useConversation(conversationId);

  // 处理错误提示
  useEffect(() => {
    if (error) {
      toast({
        title: '出错了',
        description: error,
        status: 'error',
        duration: 5000,
        isClosable: true
      });
    }
  }, [error, toast]);

  // 创建新会话
  const handleNewConversation = async () => {
    try {
      const newConversation = await startNewConversation();
      navigate(`/conversation/${newConversation.id}`);
    } catch (err) {
      toast({
        title: '创建会话失败',
        description: err.message,
        status: 'error',
        duration: 3000,
        isClosable: true
      });
    }
  };

  // 处理消息发送
  const handleSendMessage = async (message) => {
    try {
      await sendMessage(message);
    } catch (err) {
      toast({
        title: '发送失败',
        description: err.message,
        status: 'error',
        duration: 3000,
        isClosable: true
      });
    }
  };

  // 处理确认期望
  const handleConfirmExpectation = async (expectation) => {
    try {
      await confirmExpectation(expectation.id);
      toast({
        title: '期望已确认',
        status: 'success',
        duration: 2000
      });
    } catch (err) {
      toast({
        title: '确认失败',
        description: err.message,
        status: 'error',
        duration: 3000,
        isClosable: true
      });
    }
  };

  // 处理生成代码
  const handleGenerateCode = async (expectationId) => {
    try {
      const generation = await generateCode(expectationId);
      navigate(`/generations/${generation.id}`);
    } catch (err) {
      toast({
        title: '生成失败',
        description: err.message,
        status: 'error',
        duration: 3000,
        isClosable: true
      });
    }
  };

  // 渲染面包屑导航
  const renderBreadcrumb = () => (
    <Breadcrumb 
      separator={<ChevronRightIcon color="gray.500" />} 
      mb={4} 
      fontSize="sm"
    >
      <BreadcrumbItem>
        <BreadcrumbLink href="/">首页</BreadcrumbLink>
      </BreadcrumbItem>
      <BreadcrumbItem isCurrentPage>
        <BreadcrumbLink>
          {conversationId ? `会话 ${conversationId.slice(0, 8)}...` : '新会话'}
        </BreadcrumbLink>
      </BreadcrumbItem>
    </Breadcrumb>
  );

  return (
    <Container maxW="container.xl" py={4}>
      {renderBreadcrumb()}

      <Flex justify="space-between" align="center" mb={4}>
        <Heading size="lg">
          {conversationId ? '继续对话' : '开始新对话'}
        </Heading>

        <Button 
          leftIcon={<AddIcon />} 
          colorScheme="blue" 
          onClick={handleNewConversation}
        >
          新会话
        </Button>
      </Flex>

      {!conversationId ? (
        // 没有会话ID时显示欢迎信息
        <Box textAlign="center" py={20}>
          <Heading size="xl" mb={4}>欢迎使用Expeta对话式需求收集</Heading>
          <Text fontSize="lg" mb={8}>通过自然对话方式表达您的需求，我们将帮您生成高质量代码</Text>
          <Button 
            size="lg" 
            colorScheme="blue" 
            onClick={handleNewConversation}
          >
            开始新对话
          </Button>
        </Box>
      ) : (
        // 有会话ID时显示对话面板
        <Box h="calc(100vh - 200px)">
          <ConversationPanel
            conversationId={conversationId}
            initialMessages={messages}
            initialState={conversation?.state || ConversationStates.INITIAL}
            initialExpectation={conversation?.currentExpectation}
            onSendMessage={handleSendMessage}
            onConfirmExpectation={handleConfirmExpectation}
            onEditExpectation={(exp) => navigate(`/expectations/${exp.id}/edit`)}
            onGenerateCode={handleGenerateCode}
            loading={loading}
          />
        </Box>
      )}
    </Container>
  );
};

export default ConversationPage;
